  use Agent

  @doc """
  Starts a new weather icon bucket.
  """
  def start_link(opts \\ []) do
    {offset_x, offset_y} = Access.get(opts, :offset, {200, 80})

    state = %{icons: %{}, offset_x: offset_x, offset_y: offset_y}
    Agent.start_link(fn -> state end, name: __MODULE__)
  end

  def get(icon_name, default \\ nil) do
    fetch!(icon_name)
  rescue
    _ -> default
  end

  def fetch!(icon_name) do
    case Agent.get(__MODULE__, &Map.get(&1.icons, icon_name)) do
      nil ->
        @pixels
        |> Map.fetch!(icon_name)
        |> build_weather_icon_for_inky()
        |> Kernel.tap(&save_icon(icon_name, &1))

      weather_icon ->
        weather_icon
    end
  end

  # Save an icon for later use.
  defp save_icon(icon_name, weather_icon) do
    Agent.update(__MODULE__, fn state ->
      state |> put_in([:icons, icon_name], weather_icon)
    end)
  end

  # Builds Inky-compatible pixels data.
  defp build_weather_icon_for_inky(pixels, opts \\ []) do
    {offset_x, offset_y} = Access.get(opts, :offset, {200, 80})

    pixels
    |> Enum.with_index()
    |> Enum.flat_map(fn {row, index_y} ->
      row
      |> Enum.with_index()
      |> Enum.map(fn
        {0, index_x} -> {{index_x + offset_x, index_y + offset_y}, :white}
        {_, index_x} -> {{index_x + offset_x, index_y + offset_y}, :black}
      end)
    end)
    |> Enum.into(%{})
  end

  # Some possible weather descriptions:
  # https://github.com/chubin/wttr.in/blob/master/lib/constants.py
  def get_weather_icon_name(weather_description) do
    cond do
      String.match?(weather_description, ~r/sun|clear/i) -> :sun
      String.match?(weather_description, ~r/cloud|overcast/i) -> :cloud
      String.match?(weather_description, ~r/thunder/i) -> :storm
      String.match?(weather_description, ~r/snow|sleet/i) -> :snow
      String.match?(weather_description, ~r/rain|shower/i) -> :rain
      true -> nil
    end
  end
end
